multisurvivalBase <- if (requireNamespace("jmvcore", quietly=TRUE)) R6::R6Class(
    "multisurvivalBase",
    inherit = jmvcore::Analysis,
    public = list(
        initialize = function(options, data=NULL, datasetId="", analysisId="", revision=0) {
            super$initialize(
                package = "jsurvival",
                name = "multisurvival",
                version = c(1,0,0),
                options = options,
                results = multisurvivalResults$new(options=options),
                data = data,
                datasetId = datasetId,
                analysisId = analysisId,
                revision = revision,
                pause = NULL,
                completeWhenFilled = FALSE,
                requiresMissings = FALSE,
                weightsSupport = 'none')
        }))

#' Multivariable Survival Analysis
#'
#' Function for Multivariable Survival Analysis using Cox-regression.
#'
#' @examples
#' \donttest{
#' # example will be added
#'}
#' @param data The data as a data frame.
#' @param elapsedtime .
#' @param tint .
#' @param dxdate .
#' @param fudate .
#' @param outcome .
#' @param outcomeLevel .
#' @param dod .
#' @param dooc .
#' @param awd .
#' @param awod .
#' @param explanatory .
#' @param contexpl .
#' @param multievent .
#' @param analysistype .
#' @param timetypedata select the time type in data
#' @param timetypeoutput select the time type in output
#' @param uselandmark .
#' @param landmark .
#' @param hr .
#' @param sty .
#' @param ph_cox .
#' @param calculateRiskScore .
#' @param plotRiskGroups .
#' @param ac .
#' @param adjexplanatory .
#' @param km .
#' @param endplot .
#' @param byplot .
#' @param ci95 .
#' @param risktable .
#' @param censored .
#' @param pplot .
#' @param ac_method Method for computing adjusted survival curves
#' @param ac_summary Calculate and display summary statistics for adjusted
#'   curves
#' @param ac_timepoints Timepoints for calculating summary statistics
#'   (comma-separated)
#' @param ac_compare Perform statistical comparison between adjusted curves
#' @param reduced_explanatory .
#' @param compare_models .
#' @param use_modelSelection .
#' @param modelSelection .
#' @param selectionCriteria .
#' @param pEntry .
#' @param pRemoval .
#' @param use_stratify Use stratification when proportional hazards assumption
#'   is violated.
#' @param stratvar Variables to stratify by. Use when proportional hazards
#'   assumption is violated.
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$todo} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$text} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$text2} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot3} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$cox_ph} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$plot8} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$riskSummaryTable} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$riskScoreText} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$plotKM} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$riskScoreTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$riskScoreMetrics} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$riskGroupPlot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$calculatedtime} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$outcomeredefined} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$addRiskScore} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$addRiskGroup} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$adj_curves} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$adj_stats} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$plot_adj} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$adjustedSummaryTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$adjustedComparison} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$model_comparison} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$reduced_model_metrics} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$text_model_selection} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$text2_model_selection} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$selectionSteps} \tab \tab \tab \tab \tab a table \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$riskScoreTable$asDF}
#'
#' \code{as.data.frame(results$riskScoreTable)}
#'
#' @export
multisurvival <- function(
    data,
    elapsedtime,
    tint = FALSE,
    dxdate,
    fudate,
    outcome,
    outcomeLevel,
    dod,
    dooc,
    awd,
    awod,
    explanatory,
    contexpl,
    multievent = FALSE,
    analysistype = "overall",
    timetypedata = "ymd",
    timetypeoutput = "months",
    uselandmark = FALSE,
    landmark = 3,
    hr = FALSE,
    sty = "t1",
    ph_cox = FALSE,
    calculateRiskScore = FALSE,
    plotRiskGroups = FALSE,
    ac = FALSE,
    adjexplanatory,
    km = FALSE,
    endplot = 60,
    byplot = 12,
    ci95 = FALSE,
    risktable = FALSE,
    censored = FALSE,
    pplot = TRUE,
    ac_method = "average",
    ac_summary = FALSE,
    ac_timepoints = "12, 36, 60",
    ac_compare = FALSE,
    reduced_explanatory,
    compare_models = FALSE,
    use_modelSelection = FALSE,
    modelSelection = "enter",
    selectionCriteria = "aic",
    pEntry = 0.05,
    pRemoval = 0.1,
    use_stratify = FALSE,
    stratvar) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("multisurvival requires jmvcore to be installed (restart may be required)")

    if ( ! missing(elapsedtime)) elapsedtime <- jmvcore::resolveQuo(jmvcore::enquo(elapsedtime))
    if ( ! missing(dxdate)) dxdate <- jmvcore::resolveQuo(jmvcore::enquo(dxdate))
    if ( ! missing(fudate)) fudate <- jmvcore::resolveQuo(jmvcore::enquo(fudate))
    if ( ! missing(outcome)) outcome <- jmvcore::resolveQuo(jmvcore::enquo(outcome))
    if ( ! missing(explanatory)) explanatory <- jmvcore::resolveQuo(jmvcore::enquo(explanatory))
    if ( ! missing(contexpl)) contexpl <- jmvcore::resolveQuo(jmvcore::enquo(contexpl))
    if ( ! missing(adjexplanatory)) adjexplanatory <- jmvcore::resolveQuo(jmvcore::enquo(adjexplanatory))
    if ( ! missing(reduced_explanatory)) reduced_explanatory <- jmvcore::resolveQuo(jmvcore::enquo(reduced_explanatory))
    if ( ! missing(stratvar)) stratvar <- jmvcore::resolveQuo(jmvcore::enquo(stratvar))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(elapsedtime), elapsedtime, NULL),
            `if`( ! missing(dxdate), dxdate, NULL),
            `if`( ! missing(fudate), fudate, NULL),
            `if`( ! missing(outcome), outcome, NULL),
            `if`( ! missing(explanatory), explanatory, NULL),
            `if`( ! missing(contexpl), contexpl, NULL),
            `if`( ! missing(adjexplanatory), adjexplanatory, NULL),
            `if`( ! missing(reduced_explanatory), reduced_explanatory, NULL),
            `if`( ! missing(stratvar), stratvar, NULL))

    for (v in explanatory) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])
    for (v in adjexplanatory) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])
    for (v in reduced_explanatory) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])
    for (v in stratvar) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- multisurvivalOptions$new(
        elapsedtime = elapsedtime,
        tint = tint,
        dxdate = dxdate,
        fudate = fudate,
        outcome = outcome,
        outcomeLevel = outcomeLevel,
        dod = dod,
        dooc = dooc,
        awd = awd,
        awod = awod,
        explanatory = explanatory,
        contexpl = contexpl,
        multievent = multievent,
        analysistype = analysistype,
        timetypedata = timetypedata,
        timetypeoutput = timetypeoutput,
        uselandmark = uselandmark,
        landmark = landmark,
        hr = hr,
        sty = sty,
        ph_cox = ph_cox,
        calculateRiskScore = calculateRiskScore,
        plotRiskGroups = plotRiskGroups,
        ac = ac,
        adjexplanatory = adjexplanatory,
        km = km,
        endplot = endplot,
        byplot = byplot,
        ci95 = ci95,
        risktable = risktable,
        censored = censored,
        pplot = pplot,
        ac_method = ac_method,
        ac_summary = ac_summary,
        ac_timepoints = ac_timepoints,
        ac_compare = ac_compare,
        reduced_explanatory = reduced_explanatory,
        compare_models = compare_models,
        use_modelSelection = use_modelSelection,
        modelSelection = modelSelection,
        selectionCriteria = selectionCriteria,
        pEntry = pEntry,
        pRemoval = pRemoval,
        use_stratify = use_stratify,
        stratvar = stratvar)

    analysis <- multisurvivalClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

